$(document).ready(function(){displayTime();startClock();$("nav li").click(function(){var a=$(this).text().toLowerCase();$(this).siblings(".active").removeClass("active");$(this).addClass("active");$('code:not([data-type="'+a+'"])').removeClass("active");$('code[data-type="'+a+'"]').addClass("active")});$("code").each(function(a,b){hljs.highlightBlock(b)})});function startClock(){var d=new Date(),e=d.getHours(),f=d.getMinutes(),g=d.getSeconds(),c=$(".hour"),i=$(".minute"),a=$(".sekond"),b=(e>12?(e-12)*30:e*30)+f/2,h=f*6,j=g*6;c.css("transform","rotate("+b+"deg)");i.css("transform","rotate("+h+"deg)");a.css("transform","rotate("+j+"deg)");setTimeout(startClock,1000)}function displayTime(){var c=new Date();var a=chekTime(c.getHours());var b=chekTime(c.getMinutes());var d=chekTime(c.getSeconds());$(".digital-watch").html(a+":"+b+":"+d);setTimeout(function(){displayTime()},1000)}function chekTime(a){return(a<10?"0"+a:a)};